          function getPosts(e) {
            if (e.feed.entry) {
              for (var t = 0; t < e.feed.entry.length; t++) {
                var entry = e.feed.entry[t];
                for (var a = 0; a < e.feed.entry[t].link.length; a++) {
                  if ('alternate' == entry.link[a].rel) {
                    var post_url = entry.link[a].href;
                    break
                  }
                }
                var post_title = entry.title.$t,
                  post_published = entry.published.$t.substring(8, 10) + ' thag ' + entry.published.$t.substring(5, 7) + ', ' + entry.published.$t.substring(0, 4);
                if ('media$thumbnail' in entry) {
                  var post_thumb = entry.media$thumbnail.url.replace('s72-c', 's1600')
                } else {
                  post_thumb = "https://4.bp.blogspot.com/-00O66C-eBQs/W0IcokXSnOI/AAAAAAAAL_k/g4KtDm7SkQsoe7_G0vZ_C_nU0Gf_-kyVQCLcBGAs/s1600/safe_image.png"
                }
                if ('category' in entry) {
                  for (var r = 0; r < entry.category.length; r++) {
                    var label_name = entry.category[r].term,
                      label_url = '/search/label/' + label_name;
                  }
                }
                if ('summary' in entry) {
                  var post_snippet = entry.summary.$t,
                    post_snippet_length = 150,
                    post_summary = '',
                    re = /<\S[^>]*>/g,
                    post_snippet = post_snippet.replace(re, '');
                  if (post_snippet.length < post_snippet_length) {
                  post_summary = post_snippet;
                  } else {
                    post_snippet = post_snippet.substring(0, post_snippet_length);
                    var quoteEnd = post_snippet.lastIndexOf(' ');
                    post_snippet = post_snippet.substring(0, quoteEnd);
                    post_summary = post_snippet;
                  }
                } else {
                  post_summary = '';
                }
                var canonical_url = $("link[rel='canonical']").attr("href")
                if (canonical_url === post_url) {
                  $('#related-posts ul').append('<article class="post current"><div class="post_thumb"><a href=' + post_url + ' title="' + post_title + '"><img alt="' + post_title + '" src=' + post_thumb + ' /></a></div><div class="post_title"><a href=' + post_url + ' title="' + post_title + '">' + post_title + '</a></div><div class="post_meta"><span class="post_label"><a href='+ label_url +' title="'+ label_name +'">'+ label_name +'</a></span><span class="post_published">'+ post_published +'</span></div><div class="post_summary"><p>'+ post_summary +'...</p></div></article>');
                  $('#related-posts ul').find('.post.current').remove();
                } else {
                  $('#related-posts ul').append('<article class="post"><div class="post_thumb"><a href=' + post_url + ' title="' + post_title + '"><img alt="' + post_title + '" src=' + post_thumb + ' /></a></div><div class="post_title"><a href=' + post_url + ' title="' + post_title + '">' + post_title + '</a></div><div class="post_meta"><span class="post_label"><a href='+ label_url +' title="'+ label_name +'">'+ label_name +'</a></span><span class="post_published">'+ post_published +'</span></div><div class="post_summary"><p>'+ post_summary +'...</p></div></article>');
                }
              }
            }
          }
          var content = false;
          $(document).on('scroll', function() {
            if ($(this).scrollTop() >= $('.post-body').position().top) {
              if (!content) {
                content = true;
                var numposts = 10;
                $.ajax({
                  type: 'GET',
                  url: '/feeds/posts/summary/-/' + labelname,
                  data: {
                    'max-results': numposts,
                    'alt': 'json'
                  },
                  dataType: 'jsonp',
                  jsonp: 'callback',
                  jsonpCallback: 'getPosts',
                  success: function(e) {
                    $("#related-posts ul").removeClass("spinner").removeClass("load")
                    var totalposts = e.feed.openSearch$totalResults.$t                 
                    $('#allposts').attr('value', totalposts)
                    if (totalposts > numposts) {
                      $('.load-posts').removeClass('hidden')
                      $('.load-posts').click(function() {
                        $('.loadposts').removeClass('hidden').addClass('spinner').addClass('load')
                        var items = Number($('#numposts').val());
                        items = items + numposts;
                        if (items < totalposts) {
                          $('.load-posts').addClass('hidden')
                          $('#numposts').val(items);
                          setTimeout(function() {
                            $.ajax({
                              type: 'GET',
                              url: '/feeds/posts/summary/-/' + labelname,
                              data: {
                                'max-results': numposts,
                                'start-index': items + 1,
                                'alt': 'json'
                              },
                              dataType: 'jsonp',
                              jsonp: 'callback',
                              jsonpCallback: 'getPosts',
                              success: function() {
                                var lastposts = items + numposts;
                                if (lastposts < totalposts) {
                                  $('.load-posts').removeClass('hidden')
                                  $('.loadposts').addClass('hidden').removeClass('spinner').removeClass('load')
                                } else {
                                  $('.loadposts,.load-post,.show-posts').addClass('hidden')                                
                                }
                              }
                            })
                          }, 500)
                        } else {
                          $('.load-posts,.loadposts,.show-posts').addClass('hidden')
                        }
                      })
                    } else {
                      $('.show-posts').addClass('hidden')
                    }
                  }
                })
              }
            }
          })
