    function recentposts(e) {
      if (e.feed.entry) {
        for (var t = 0; t < e.feed.entry.length; t++) {
          var entry = e.feed.entry[t];
          for (var a = 0; a < entry.link.length; a++)
            if ("alternate" == entry.link[a].rel) {
              var s = entry.link[a].href;
              break
            }
          for (var r = 0; r < entry.category.length; r++) {
            var l = entry.category[r].term,
              n = "/search/label/" + l;
          }
          var d = entry.title.$t,
            p = entry.published.$t,
            c = p.substring(8, 10) + " thag " + p.substring(5, 7) + ", " + p.substring(0, 4);
          if ('media$thumbnail' in entry) {
            var i = entry.media$thumbnail.url.replace("s72-c", "s1600")
          } else {
            i = "https://4.bp.blogspot.com/-00O66C-eBQs/W0IcokXSnOI/AAAAAAAAL_k/g4KtDm7SkQsoe7_G0vZ_C_nU0Gf_-kyVQCLcBGAs/s1600/safe_image.png"
          }
          var f = entry.summary.$t,
            g = 150,
            r = /<\S[^>]*>/g,
            x = '',
            f = f.replace(r, "");
          if (f.length < g) {
            x = f;
          } else {
            f = f.substring(0, g);
            var quoteEnd = f.lastIndexOf(" "),
            f = f.substring(0, quoteEnd),
            x = f;
          }
          var z = $("link[rel='canonical']").attr('href')
          if (z === s) {
            $('.recent-posts ul').append('<li class="current"><div class="item-thumbnail"><a href='+ s +' title="'+ d +'"><img alt="'+ d +'" src='+ i +' /></a></div><div class="item-title"><a href='+ s +' title="'+ d +'">'+ d +'</a></div><div class="item-meta"><span class="item-label"><a href='+ n +' title="'+ l +'">'+ l +'</a></span><span class="item-published">'+ c +'</span></div><div class="item-snippet"><p>'+ x +'...</p></div></li>')
            $('.recent-posts ul').find($('.current')).remove()
          } else {
            $('.recent-posts ul').append('<li><div class="item-thumbnail"><a href='+ s +' title="'+ d +'"><img alt="'+ d +'" src='+ i +' /></a></div><div class="item-title"><a href='+ s +' title="'+ d +'">'+ d +'</a></div><div class="item-meta"><span class="item-label"><a href='+ n +' title="'+ l +'">'+ l +'</a></span><span class="item-published">'+ c +'</span></div><div class="item-snippet"><p>'+ x +'...</p></div></li>')
          }
        }
      }
    }
