    function getComments(e) {
      if (e.feed.entry)
        for (var t = 0; t < e.feed.entry.length; t++) {
          var entry = e.feed.entry[t];
          for (var a = 0; a < entry.link.length; a++) {
            if ("alternate" == entry.link[a].rel) {
              var r = entry.link[a].href;
              break
            }
          } 
          var s = entry.published.$t,
            n = entry.author[0].gd$image.src.replace("https://img1.blogblog.com/img/blank.gif", "https://lh3.googleusercontent.com/zFdxGE77vvD2w5xHy6jkVuElKv-U9_9qLkRYK8OnbDeJPtjSZ82UPq5w6hJ-SA=s40"),
            c = s.substring(8, 10) + " thag " + s.substring(5, 7) + ", " + s.substring(0, 4),
            l = entry.author[0].uri.$t,
            o = (entry.title.$t, entry.author[0].name.$t),
            i = entry.summary.$t.substring(0, 150);
          $(".recent-comments ul").append('<li><div class="rc_avatar"><img src=' + n + ' alt="' + o + '" /></div><div class="rc_block"><div class="rc_header"><span class="rc_user"><a rel="nofollow" href=' + l + ' title="' + o + '" target="_blank">' + o + '</a></span><span class="rc_date"><a rel="nofollow" href=' + r + ">" + c + '</a></span></div><p class="rc_content">' + ((m = i) && (m = m.replace(/((https?\:\/\/)|(www\.))(\S+)(\w{2,4})(:[0-9]+)?(\/|\/([\w#!:.?+=&%@!\-\/]))?/gi, function(e) {
            var t = e;
            return t.match("^https?://") || (t = "http://" + t), '<a href="' + t + '">' + e + "</a>"
          })), m) + "</p><p class='rc_more'><a rel='nofollow' href=" + r + ">" + rc_text + "</a></p></div></li>")
        }
      var m;
      bodyText = document.getElementsByClassName("recent-comments")[0], text = bodyText.innerHTML, text = text.replace(/=ok/gi, "👌"), text = text.replace(/=hi/gi, "✌"), text = text.replace(/\(y\)/gi, "👍"), text = text.replace(/\(yy\)/gi, "👎"), text = text.replace(/=hl/gi, "👈"), text = text.replace(/=hr/gi, "👉"), text = text.replace(/=hup/gi, "👆"), text = text.replace(/=hd/gi, "👇"), text = text.replace(/=j/gi, "??"), text = text.replace(/=clap/gi, "👏"), text = text.replace(/&lt;3/gi, "💗"), text = text.replace(/=he/gi, "😍"), text = text.replace(/:3/gi, "😂"), text = text.replace(/:\)\)/gi, "😅"), text = text.replace(/;\)/gi, "😉"), text = text.replace(/:D/gi, "😁"), text = text.replace(/=D/gi, "😃"), text = text.replace(/:\)/gi, "🙂"), text = text.replace(/\/\=r/gi, "😏"), text = text.replace(/:\(\(/gi, "😭"), text = text.replace(/:\(/gi, "😞"), text = text.replace(/==/gi, "😓"), text = text.replace(/:\*/gi, "😘"), text = text.replace(/\^\_\^/gi, "😊"), text = text.replace(/:\-\P/gi, "😛"), text = text.replace(/:P/gi, "😜"), text = text.replace(/\/=l/gi, "😒"), text = text.replace(/:v/gi, "😬"), text = text.replace(/\:\-\)/gi, "😌"), text = text.replace(/8\-\)/gi, "🤓"), text = text.replace(/8\|/gi, "😎"), text = text.replace(/:\\/gi, "😕"), text = text.replace(/:\'\(/gi, "😢"), bodyText.innerHTML = text
    }
