// Section G Multi Tabs
function loadTab(soop, coop) {
var numOfOP = 6;
var ChroCount = 92;
var slidert = $(coop).text().trim();
var latestrop = $(soop);
var titlevv = $('.secop h2');
$('.secop .dmore, .secop .loadera').remove();
titlevv.after('<span class="loadera"><i class="fa fa-circle-o-notch fa-spin"></i></span><span class="dmore"> <a href=\"/search/label/'+ slidert +'\">more</a> </span>');
var doopss = "/-/" + slidert;

  if (slidert.toUpperCase() == 'LATEST') {
doopss = "";  
$('.secop .dmore, .secop .loadera').remove();
titlevv.after('<span class="loadera"><i class="fa fa-circle-o-notch fa-spin"></i></span><span class="dmore"> <a href=\"/search/label/\">more</a> </span>');
}
var tood0 = ""; var tood1 = ""; var tood2 = ""; var tood3 = ""; var tood4 = ""; var tood5 = "";
$('.secop .loadera').show();
  $.ajax({
       url: '/feeds/posts/default' + doopss + '?alt=json-in-script&max-results='+ numOfOP +'',
        type: "get",
        dataType: "jsonp",
        success: function (e) {

          var img  = new Array();

          for (var i = 0; i < 10; i++) {
            var entry = e.feed.entry[i];
            var posttitle = entry.title.$t;
            var posturl;


var category = "";

         category += '<a class="post-category" href="/search/label/'+entry.category[0].term+'?max-results=7">'+entry.category[0].term+'</a>';

            for (var k = 0; k < entry.link.length; k++) {
              if (entry.link[k].rel == 'alternate') {
                posturl = entry.link[k].href;
                break;
              }
            }

    var ListMonth= ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];

var ListDate= entry.published.$t.substring(0,10);

                       var  Y = ListDate.substring(0, 4);
                       var m = ListDate.substring(5, 7);
                        var D = ListDate.substring(8, 10);
                        var M = ListMonth[parseInt(m - 1)];                       


    var ListDateOld= entry.published.$t.substring(0,19);

            if ("content" in entry) {
              var postcontent = entry.content.$t;
            }

            s = postcontent; a = s.indexOf("<img"); b = s.indexOf("src=\"",a); c = s.indexOf("\"",b+5); d = s.substr(b+5,c-b-5);

            if((a!=-1)&&(b!=-1)&&(c!=-1)&&(d!="")) img[i] = d;

            var tmb = img[i].replace('s1600/','s300/');

var conten = entry.content.$t;
var mycontent= conten.replace(/(<([^>]+)>)/ig,"").substring(0, ChroCount);
mycontent = mycontent.substr(0, Math.min(mycontent.length, mycontent.lastIndexOf(" ")))
 			if(i===0) {
              tood0 = '<a href='+ posturl +'><div class="lazy opp" data-original='+ tmb +' style="background: #444444 url() no-repeat center;background-size: cover">' + '</div></a><div class="bwrap">' + category +'<a href="'+ posturl +'" class="oapost-title"><h3 class="heading">'+ posttitle +'</h3></a>' + '<time class="timeago" datetime='+ListDateOld+'>' + M + ' ' + D + ', ' + Y + '</time><div class="contento">' + mycontent + '</div></div>';
            }
            if(i===1) {
              tood1 = '<a href='+ posturl +'><div class="lazy opp" data-original='+ tmb +' style="background: #444444 url() no-repeat center;background-size: cover">' + '</div></a><div class="bwrap">' + category +'<a href="'+ posturl +'" class="obpost-title"><h3 class="heading">'+ posttitle +'</h3></a>' + '<time class="timeago" datetime='+ListDateOld+'>' + M + ' ' + D + ', ' + Y + '</time><div class="contento">' + mycontent + '</div></div>';
            }
            if(i===2) {
              tood2 = '<a href='+ posturl +'><div class="lazy opp oppo" data-original='+ tmb +' style="background: #444444 url() no-repeat center;background-size: cover">' + '</div></a><div class="bwrap">' + category +'<a href="'+ posturl +'" class="ocpost-title"><h3 class="heading">'+ posttitle +'</h3></a>' + '<time class="timeago" datetime='+ListDateOld+'>' + M + ' ' + D + ', ' + Y + '</time><div class="contento">' + mycontent + '</div></div>';
            }
            if(i===3) {
              tood3 = '<a href='+ posturl +'><div class="lazy opp" data-original='+ tmb +' style="background: #444444 url() no-repeat center;background-size: cover">' + '</div></a><div class="bwrap">' + category +'<a href="'+ posturl +'" class="odpost-title"><h3 class="heading">'+ posttitle +'</h3></a>' + '<time class="timeago" datetime='+ListDateOld+'>' + M + ' ' + D + ', ' + Y + '</time><div class="contento">' + mycontent + '</div></div>';
            }
			if(i===4) {
              tood4 = '<a href='+ posturl +'><div class="lazy opp" data-original='+ tmb +' style="background: #444444 url() no-repeat center;background-size: cover">' + '</div></a><div class="bwrap">' + category +'<a href="'+ posturl +'" class="odpost-title"><h3 class="heading">'+ posttitle +'</h3></a>' + '<time class="timeago" datetime='+ListDateOld+'>' + M + ' ' + D + ', ' + Y + '</time><div class="contento">' + mycontent + '</div></div>';
            }
			if(i===5) {
              tood5 = '<a href='+ posturl +'><div class="lazy opp oppo" data-original='+ tmb +' style="background: #444444 url() no-repeat center;background-size: cover">' + '</div></a><div class="bwrap">' + category +'<a href="'+ posturl +'" class="odpost-title"><h3 class="heading">'+ posttitle +'</h3></a>' + '<time class="timeago" datetime='+ListDateOld+'>' + M + ' ' + D + ', ' + Y + '</time><div class="contento">' + mycontent + '</div></div>';
            }
             latestrop.html("<div class='opi'><div class='op1 opall'>" + tood0 + "<div class='clear'/></div><div class='op2 opall'>" + tood1 + "<div class='clear'/></div><div class='op3 opx3 opall'>" + tood2 + "<div class='clear'/></div>" + "<div class='plear'/><div class='op4 opall'>" + tood3 + "<div class='clear'/></div><div class='op5 opall'>" + tood4 + "<div class='clear'/></div><div class='op6 opx3 opall'>" + tood5 + "<div class='clear'/></div><div class='plear'/></div>"); 
$('.secop .loadera').hide();
$('.secop .widget-content > div').hide();
$(soop).show();
$(".lazy.opp").lazyload();  
          }
        }

      })
}
