//
 loadTab('.secop .a1', '.secop .a');
    $(".secop .dropdown-menu li").on('click', function(event){
	event.stopPropagation();
	var piis = $(this).text().trim();
	$(this).closest('.widget').find('h2').text(piis);
	$(this).closest('.dropdown-menu').slideUp("fast");
	var pcox = $(this).children('a').attr('class');
	$(this).closest('.dropdown-menu').hide();
	var ppux = (".secop ."+pcox);
	var xcox = $(this).closest('.secop').find("."+pcox+"1");
    loadTab(xcox, ppux);
});
