  function bypass(fst, snd) {
var numOfOPymn = 5;
var ChrmCountn = 92;
var latestrymn = $(fst);
var puuyi = $(snd).text().trim();
var titleoo = $('.secsp h2');
$('.secsp .dmore, .secsp .loadera').remove();
titleoo.after('<span class="loadera"><i class="fa fa-circle-o-notch fa-spin"></i></span><span class="dmore"> <a href=\"/search/label/'+ puuyi +'\">more</a> </span>');
var doopsi = "/-/" + puuyi;
  if (puuyi.toUpperCase == 'LATEST') {
doopsi = "";  
$('.secsp .dmore, .secsp .loadera').remove();
titleoo.after('<span class="loadera"><i class="fa fa-circle-o-notch fa-spin"></i></span><span class="dmore"> <a href=\"/search/label/\">more</a> </span>');
}
$('.secsp .loadera').show();
var txxd1 = ""; var txxd2 = ""; var txxd3 = "";
  $.ajax({
		  url: "/feeds/posts/default" + doopsi + "?alt=json-in-script&max-results="+ numOfOPymn +"",
        type: "get",
        dataType: "jsonp",
        success: function (e) {

          var img  = new Array();

          for (var i = 0; i < 10; i++) {
            var entry = e.feed.entry[i];
            var posttitle = entry.title.$t;
            var posturl;


 var category = '<a class="post-category" href="/search/label/'+entry.category[0].term+'?max-results=7">'+entry.category[0].term+'</a>';	

            for (var k = 0; k < entry.link.length; k++) {
              if (entry.link[k].rel == 'alternate') {
                posturl = entry.link[k].href;
                break;
              }
            }

            if ("content" in entry) {
              var postcontent = entry.content.$t;
            }

            s = postcontent; a = s.indexOf("<img"); b = s.indexOf("src=\"",a); c = s.indexOf("\"",b+5); d = s.substr(b+5,c-b-5);

            if((a!=-1)&&(b!=-1)&&(c!=-1)&&(d!="")) img[i] = d;

            var tmb = img[i].replace('s1600/','s600/');
			var lmb = img[i].replace('s1600/','s300/');
 			if(i===0) {
              txxd1 = '<a href='+ posturl +'><div class="lazy zapo oppmi" data-original='+ tmb +' style="background: #444444 url() no-repeat center;background-size: cover">' + '</div></a><div class="bwrap">' + category + '<a href="'+ posturl +'" class="oay-title"><h3 class="heading">'+ posttitle +'</h3></a>' + '</div><div class="clear"/>';
            }
			if(i===1) {
              txxd2 = '<a href='+ posturl +'><div class="lazy zapo oppois" data-original='+ lmb +' style="background: #444444 url() no-repeat center;background-size: cover">' + '</div></a><div class="bwraps"><a href="'+ posturl +'" class="oay-title"><h3 class="heading">'+ posttitle +'</h3></a>' + '</div><div class="clear"/>';
            }
			if(i===2) {
              txxd3 = '<a href='+ posturl +'><div class="lazy zapo oppois" data-original='+ lmb +' style="background: #444444 url() no-repeat center;background-size: cover">' + '</div></a><div class="bwraps"><a href="'+ posturl +'" class="oay-title"><h3 class="heading">'+ posttitle +'</h3></a>' + '</div><div class="clear"/>';
            }
             latestrymn.html("<div class='nopymi'><div class='opymi'><div class='xhid'>" + txxd1 + "</div></div><div class='opyni'><div class='opyn1'>" + txxd2 + "</div><div class='opyn2'>" + txxd3 + "</div></div><div class='clear'/></div>" ); 
$('.secsp .loadera').hide();
$('.secsp .widget-content > div').hide();
$(fst).show();
$(".lazy.zapo").lazyload();   
          }

        }

      })
  }
