  function seclast(secaa, secbb) {
var numOfPouk = 4;
var latestrpouk = $(secaa);
var latestcwnok = $(secbb).text().trim();
var titleosk = $('.secvp h2');
$('.secvp .dmore, .secvp .loadera').remove();
titleosk.after('<span class="loadera"><i class="fa fa-circle-o-notch fa-spin"></i></span><span class="dmore"> <a href=\"/search/label/'+ latestcwnok +'\">more</a> </span>');
var doopsk = "/-/" + latestcwnok;
  if (latestcwnok.toUpperCase == 'LATEST') {
doopsk = "";  
$('.secvp .dmore, .secvp .loadera').remove();
titleosk.after('<span class="loadera"><i class="fa fa-circle-o-notch fa-spin"></i></span><span class="dmore"> <a href=\"/search/label/\">more</a> </span>');
}
$('.secvp .loadera').show();
var pxcc1=""; var pxcc2=""; var pxcc3=""; var pxcc4="";
  $.ajax({
        url: "/feeds/posts/default" + doopsk + "?alt=json-in-script&max-results="+ numOfPouk +"",
        type: "get",
        dataType: "jsonp",
        success: function (e) {

          var img  = new Array();

          for (var i = 0; i < 10; i++) {
            var entry = e.feed.entry[i];
            var posttitle = entry.title.$t;
            var posturl;


            for (var k = 0; k < entry.link.length; k++) {
              if (entry.link[k].rel == 'alternate') {
                posturl = entry.link[k].href;
                break;
              }
            }

            if ("content" in entry) {
              var postcontent = entry.content.$t;
            }

            s = postcontent; a = s.indexOf("<img"); b = s.indexOf("src=\"",a); c = s.indexOf("\"",b+5); d = s.substr(b+5,c-b-5);

            if((a!=-1)&&(b!=-1)&&(c!=-1)&&(d!="")) img[i] = d;

            var tmb = img[i].replace('s1600/','s300/');

 			if(i===0) {
              pxcc1 = '<div class="fft1 fft"><a href='+ posturl +'><div class="lazy mluy" data-original='+tmb+' style="background: #ededed url() no-repeat center;background-size: cover">' + '</div></a><div class="bwrap"><a href="'+ posturl +'" class="mlpost-title"><h3 class="heading">'+ posttitle +'</h3></a></div></div>';
            }
			if(i===1) {
              pxcc2 = '<div class="fft2 fft"><a href='+ posturl +'><div class="lazy mluy" data-original='+ tmb +' style="background: #ededed url() no-repeat center;background-size: cover">' + '</div></a><div class="bwrap"><a href="'+ posturl +'" class="mlpost-title"><h3 class="heading">'+ posttitle +'</h3></a></div></div>';
            }
			if(i===2) {
              pxcc3 = '<div class="fft3 fft"><a href='+ posturl +'><div class="lazy mluy" data-original='+ tmb +' style="background: #ededed url() no-repeat center;background-size: cover">' + '</div></a><div class="bwrap"><a href="'+ posturl +'" class="mlpost-title"><h3 class="heading">'+ posttitle +'</h3></a></div></div>';
            }
           	if(i===3) {
              pxcc4 = '<div class="fft4 fft"><a href='+ posturl +'><div class="lazy mluy" data-original='+ tmb +' style="background: #ededed url() no-repeat center;background-size: cover">' + '</div></a><div class="bwrap"><a href="'+ posturl +'" class="mlpost-title"><h3 class="heading">'+ posttitle +'</h3></a></div></div>';
            }
           latestrpouk.html("<div class='mlfe'>" + pxcc1 + pxcc2 + pxcc3 + pxcc4 + "<div class='clear'/></div>"); 
$('.secvp .loadera').hide();
$('.secvp .widget-content > div').hide();
$(secaa).show(); 
$(".lazy.mluy").lazyload();        
		}
        }

      })
  }
