$(document).ready(function(){
        $('.showhide').on('click', function(event) {        
             var el = $(this);
  if (el.text() == el.data("text-swap")) {
    el.text(el.data("text-original"));
  } else {
    el.data("text-original", el.text());
    el.text(el.data("text-swap"));
  }
   $('#comments').toggle('show');
        });
    });
